func puzzle1(input string) int {
	floor := 0

	for _, v := range input {
		if v == '(' {
			floor++
		} else if v == ')' {
			floor--
		} else {
			panic(fmt.Errorf("wtf"))
		}
	}
	return floor
}

func puzzle2(input string) int {
	floor := 0

	for i, v := range input {
		if v == '(' {
			floor++
		} else if v == ')' {
			floor--
		} else {
			panic(fmt.Errorf("wtf"))
		}

		if floor == -1 {
			return i + 1
		}
	}
	return floor
}
